mapper.update:
update sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, url = #{url}, menu_type = #{menuType}, target = #{target}, menu_key = #{menuKey}, menu_lay = #{menuLay}, visible = #{visible}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where menu_id = #{menuId}

mapper.select:



        AND menu_key=#{menuKey}


        AND menu_name like concat('%', #{menuName}, '%')


        AND visible = #{visible}


order by order_num
